import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../Context/CartContext';

const CartPage = () => {
  const { cart, totalAmount } = useCart();
  const navigate = useNavigate();

  // הפונקציה שמנווטת לדף המוצרים
  const handleGoToProducts = () => {
    navigate('/products'); // נווט לדף המוצרים
  };

  return (
    <div className="cart-page">
      <h1>Your Cart</h1>

      {cart.length === 0 ? (
        <div>
          <p>Your cart is empty.</p>
          <button 
            className="go-to-products-btn" 
            onClick={handleGoToProducts}
          >
            Go to Products
          </button>
        </div>
      ) : (
        <div>
          <ul className="cart-items">
            {cart.map((product, index) => (
              <li key={index} className="cart-item">
                <img 
                  src={product.image} 
                  alt={product.name} 
                  className="item-image"
                />
                <div className="item-details">
                  <h3 className="item-name">{product.name}</h3>
                  <p className="item-price">₪{product.price} x {product.quantity}</p>
                </div>
              </li>
            ))}
          </ul>
          <div className="cart-summary">
            <h3 className="total">Total: ₪{totalAmount}</h3>
          </div>

          {/* כפתור חזרה לדף המוצרים */}
          <div className="back-to-products">
            <button 
              className="back-btn" 
              onClick={handleGoToProducts}
            >
              <span className="arrow">←</span> Back to Products
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CartPage;
