import React, { useContext } from 'react';
import { UserContext } from '../Context/UserContext';
import EditProducts from './EditProducts'; // import edit products component
import './AdminPanel.css'; // Import the new CSS file

const AdminPanel = () => {
  const { users, isAdmin, products, updateProduct } = useContext(UserContext);

  if (!isAdmin) {
    return <div>You are not authorized to view this page.</div>;
  }

  return (
    <div className="admin-panel">
      <h2>Admin Panel</h2>

      <h3>List of Users</h3>
      <div className="user-list">
        {users.map((user) => (
          <div key={user.email} className="user-card">
            <div className="user-name">{user.name}</div>
            <div className="user-email">{user.email}</div>
          </div>
        ))}
      </div>

      <h3>Product Editing</h3>
      <EditProducts products={products} updateProduct={updateProduct} />
    </div>
  );
};

export default AdminPanel;
