import React, { useContext } from 'react';

const AdminPanel = () => {
  const { users, isAdmin } = useContext(UserContext);

  if (!isAdmin) {
    return <div>אתה לא מנהל ואין לך גישה לפאנל זה</div>;
  }

  return (
    <div>
      <h1>פאנל המנהל</h1>
      <h2>רשימת המשתמשים:</h2>
      <ul>
        {users.map((user, index) => (
          <li key={index}>{user.name} ({user.email})</li>
        ))}
      </ul>
    </div>
  );
};
export default AdminPanel;
