import React from 'react';
import { Link } from 'react-router-dom';
import logo from '../images/logo.jpg'; // נתיב לתמונה
import { useContext } from 'react';
import { UserContext } from '../Context/UserContext';

import '../css/Navbar.css';

const Navbar = () => {
  return (
    <nav className="navbar">
      <div className="logo">
        <Link to="/">
          <img src={logo} alt="Pack for Camp Logo" />
        </Link>
      </div>
      <div className="nav-links">
        <Link to="/">Home</Link>
        <Link to="/about">About Us</Link>
        <Link to="/products">Products</Link>
        <Link to="/PerchasePge">PerchasePge</Link>
        <Link to="/cart">Cart</Link>
        <Link to="/contact">Contact Us</Link>
        <Link to="/login">Login</Link>
        <Link to="/register">Register</Link>
        {currentUser ? (
  <div className="user-controls">
  <span>שלום, {currentUser.name}</span>
   <button onClick={logoutUser} className="logout-btn">התנתקות</button>
    </div>): (
             
                <div className="auth-links">
                    <Link to="/login">התחברות</Link>
                    <Link to="/register">הרשמה</Link>
                </div>
            )}
      </div>
    </nav>
  );
};

export default Navbar;
