import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserContext } from '../Context/UserContext';
import { User } from '../Context/Users';

function Register() {
    const { addUser } = useContext(User);  // פונקציה להוספת משתמשים
    const navigate = useNavigate();
    const { registerUser } = useContext(UserContext);
    
    // שמירה על נתוני הטופס
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        password: '',
        confirmPassword: ''
    });

    // עדכון נתונים בטופס
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value
        }));
    };

    // טיפול בהרשמה
    const handleRegister = () => {
        addUser(formData);  // הוספת המשתמש החדש
        alert('הרשמה בוצעה בהצלחה');
    };

    // עדכון נתונים נוספים בטופס
    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    // טיפול בהגשת הטופס
    const handleSubmit = (e) => {
        e.preventDefault();
        if (formData.password === formData.confirmPassword) {
            registerUser(formData);  // פונקציה לרישום המשתמש
            navigate('/login');  // מעבר לדף התחברות
        } else {
            alert('הסיסמאות אינן תואמות');
        }
    };

    return (
        <div className='register-container'>
            <h2>הרשמה</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>שם מלא</label>
                    <input
                        type='text'
                        name='name'
                        value={formData.name}  // עדכון למשתנה הנכון
                        onChange={handleInputChange}
                        required
                    />
                </div>

                <div>
                    <label>אימייל</label>
                    <input
                        type='email'
                        name='email'
                        value={formData.email}  // עדכון למשתנה הנכון
                        onChange={handleInputChange}
                        required
                    />
                </div>

                <div>
                    <label>סיסמא</label>
                    <input
                        type='password'
                        name='password'
                        value={formData.password}  // עדכון למשתנה הנכון
                        onChange={handleInputChange}
                        required
                    />
                </div>

                <div>
                    <label>אימות סיסמא</label>
                    <input
                        type='password'
                        name='confirmPassword'
                        value={formData.confirmPassword}  // עדכון למשתנה הנכון
                        onChange={handleChange}
                        required
                    />
                </div>

                <button type='submit' onClick={handleRegister}>הרשמה</button>
            </form>
        </div>
    );
}

export default Register;
