import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Products from './components/ProductsPage';  // Notice ProductsPage instead of Products
import Cart from './components/CartPage';
import Login from "./components/Login";
import Register from "./components/Register";
import Home from './components/HomePage';
import AboutPage from './components/AboutPage';
import ContactPage from './components/ContactPage';
import ProductsPage from './components/ProductsPage';
import React, { useState } from 'react';



 App=()=> { 
  const [products, setProducts] = useState = ([
    {
      id: 1,
      name: 'Nalgene Water Bottle - On the Fly',
      price: 17.99,
      description: 'high-flow drinking spout thats easy to fill at the fountain or sink.',
      image: '/images/bottle.jpg'
    },
    {
      id: 2,
      name: 'Portable Clip-On Fan - Battery Operated or USB 4-Inch',
      price: 19.99,
      description: 'Keep cool in your bunk, dorm or room!',
      image: '/images/fan.jpg'
    },
    {
      id: 3,
      name: 'Plush Velour Terry Boys Bathrobe with Tie & Hood Navy',
      price: 49.99,
      description: 'Great bathrobe for walking to and from the pool, absorbent for showers.',
      image: '/images/roob.jpg'
    },
    {
      id: 4,
      name: 'Large Soft Trunk Square Duffel Bag Cargo Bag 36" - Personalization Available',
      price: 59.99,
      description: 'This large lightweight duffel bag is great for sleepaway camp and other air travel!',
      image: '/images/duffleBag.jpg'
    },
    {
      id: 5,
      name: 'Ozark Trails Folding Camp Chair',
      price: 24.99,
      description: 'This chair is perfect for use at camp, tailgating, or on the sidelines at your childrens game. Steel frame construction for durabili',
      image: '/images/CampChair.jpg'
    },
    {
      id: 6,
      name: 'Popup Shower Caddy',
      price: 9.99,
      description:'A perfect way to carry all your personal care items right into the shower!',
      image: '/images/shower.jpg'
    }
  ]);
  const [showCartPopup, setShowCartPopup] = useState(false);

  const triggerCartPopup = () => {
    setShowCartPopup(true);
    setTimeout(() => setShowCartPopup(false), 3000);
  };



  const [cartItems, setCartItems] = useState([]);
  const [totalAmount, setTotalAmount] = useState(0);
  const [cartCount, setCartCount] = useState(0);

  const addToCart = (prod) => {
    const existingItem = cartItems.find((item) => item.id === prod.id);

    if (existingItem) {
      const updatedCart = cartItems.map((item) =>
        item.id === prod.id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      );
      setCartItems(updatedCart);
    } else {
      setCartItems([...cartItems, { ...prod, quantity: 1 }]);
    }

    setTotalAmount((prevTotal) => prevTotal + prod.price);
    setCartCount((prevCount) => prevCount + 1);

    triggerCartPopup();
  };

  return (
    <Router>
      <div className="App">
        <Navbar />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<AboutPage />} />
          <Route path="/contact" element={<ContactPage />} />
          <Route path="/products" element={<ProductsPage />} />
          <Route path="/cart" element={<Cart />} />
          <Route path="/perchasePage" element={<perchasePage />} />
         <Route path="/login" element={<Login />} />
         <Route path="/register" element={<Register />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
