import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Products from './components/ProductsPage';  // Notice ProductsPage instead of Products
import Cart from './components/CartPage';
import Login from "./components/Login";
import Register from "./components/Register";
import Home from './components/HomePage';
import AboutPage from './components/AboutPage';
import ContactPage from './components/ContactPage';
import { useState } from 'react';
import ProductsPage from './components/ProductsPage';
import { UserProvider } from './Context/UserContext';


function App() { 


  return (

    <Router>
       <UserProvider>
      <div className="App">
        <Navbar cart={cart} totalAmount={totalAmount} />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<AboutPage />} />
          <Route path="/contact" element={<ContactPage />} />
          <Route path="/products" element={<ProductsPage Products={Products} addToCart={addToCart} />} />
          <Route path="/cart" element={<Cart cart={cart} removeFromCart={removeFromCart} totalAmount={totalAmount} />} />
          <Route path="/perchasePage" element={<perchasePage />} />
         <Route path="/login" element={<Login />} />
         <Route path="/register" element={<Register />} />
        </Routes>
      </div>
     </UserProvider> 
     </Router>

  );
}

export default App;
