import React from "react";
import { Link, Route, Routes } from "react-router-dom";
import HomePage from "./components/HomePage";
import AboutPage from "./components/AboutPage";
import ContactPage from "./components/ContactPage";
import CartPage from "./components/CartPage";
import PerchasePage from "./components/PerchasePage";
import ProductsPage from "./components/ProductsPage";
import ProductDetails from "./components/ProductDetails";

export const MyApp = () => {
  const Products = [
    {
      id: 1,
      name: 'Nalgene Water Bottle - On the Fly',
      price: 17.99,
      description: 'high-flow drinking spout thats easy to fill at the fountain or sink.',
      image: '/images/bottle.jpg'
    },
    {
      id: 2,
      name: 'Portable Clip-On Fan - Battery Operated or USB 4-Inch',
      price: 19.99,
      description: 'Keep cool in your bunk, dorm or room!',
      image: '/images/fan.jpg'
    },
    {
      id: 3,
      name: 'Plush Velour Terry Boys Bathrobe with Tie & Hood Navy',
      price: 49.99,
      description: 'Great bathrobe for walking to and from the pool, absorbent for showers.',
      image: '/images/roob.jpg'
    },
    {
      id: 4,
      name: 'Large Soft Trunk Square Duffel Bag Cargo Bag 36" - Personalization Available',
      price: 59.99,
      description: 'This large lightweight duffel bag is great for sleepaway camp and other air travel!',
      image: '/images/duffleBag.jpg'
    },
    {
      id: 5,
      name: 'Ozark Trails Folding Camp Chair',
      price: 24.99,
      description: 'This chair is perfect for use at camp, tailgating, or on the sidelines at your childrens game. Steel frame construction for durabili',
      image: '/images/CampChair.jpg'
    },
    {
      id: 6,
      name: 'Popup Shower Caddy',
      price: 9.99,
      description:'A perfect way to carry all your personal care items right into the shower!',
      image: '/images/shower.jpg'
    }]
    
  return (
    <>
      <header>
        <h1>Pack To Camp</h1>
        <nav>
          {/* ניווט בין הדפים באתר */}
          <Link to="/">Home</Link>
          <Link to="/about">About</Link>
          <Link to="/contact">Contact</Link>
          <Link to="/cart">Cart</Link>
          <Link to="/purchase">Perchase Page</Link>
          <Link to="/products">Products</Link>
        </nav>
      </header>
      
      <main>
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/about" element={<AboutPage />} />
          <Route path="/contact" element={<ContactPage />} />
          <Route path="/cart" element={<CartPage />} />
          <Route path="/purchase" element={<PerchasePage />} />
          <Route path="/products" element={<ProductsPage />} />
          <Route path="/product/:id" element={<ProductDetails />} />
          <Route path="/products/:id" element={<ProductDetails products={Products} addToCart={addToCart} />}/>
        </Routes>
      </main>
      
      <footer>
        <p>כל הזכויות שמורות</p>
      </footer>
    </>
  );
};
