import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../Context/CartContext';
import '../css/purchasePage.css';


const PerchasePage = () => {
  const { cart, totalAmount } = useCart();
  const [isConfirmed, setIsConfirmed] = useState(false);
  const navigate = useNavigate();

  const handleConfirmPerchase = () => {
    setIsConfirmed(true);
    setTimeout(() => {
      navigate('/'); // נווט חזרה לדף הבית או דף אחר
    }, 3000);
  };

  return (
    <div className="purchase-page">
      <h1 className="purchase-title">Purchase Summary</h1>
      {cart.length === 0 ? (
        <p className="empty-cart-message">Your cart is empty, please add items to proceed.</p>
      ) : (
        <div className="purchase-content">
          <ul className="purchase-items">
            {cart.map((product, index) => (
              <li key={index} className="purchase-item">
                <img 
                  src={product.image} 
                  alt={product.name} 
                  className="item-image"
                />
                <div className="item-details">
                  <h3 className="item-name">{product.name}</h3>
                  <p className="item-price">₪{product.price} x {product.quantity}</p>
                </div>
              </li>
            ))}
          </ul>
          <div className="purchase-summary">
            <h3 className="total">Total: ₪{totalAmount}</h3>
            <p className="invoice-info">A confirmation email with the invoice will be sent to your email address.</p>
          </div>

          {!isConfirmed ? (
            <button 
              className="confirm-btn"
              onClick={handleConfirmPerchase}
            >
              Confirm Purchase
            </button>
          ) : (
            <p className="thank-you-message">Thank you for your purchase! An invoice has been sent to your email.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default PerchasePage;
