import React, { createContext, useState, useContext } from 'react';
const UserContext1= createContext({
    users: [],
    currentUser:null,
    isAdnim:false,
});

function UserProvider({children}){
    const [users,setUsers]=useState([
{ password: 1, name: 'manager', email: 'manager@gmail.com', isAdmin: true },
{ password: 2, name: 'yaeli', email: 'y@gmail.com', isAdmin: false },
{ password: 3, name: 'miri', email: 'm@gmail.com', isAdmin: false },
{ password: 4, name: 'bimyamin', email: 'bimyamin@gmail.com', isAdmin: false },
    ]);

    const[currentUser,setCurrentUser]=useState(null);
    
    //login function
    const loginUser=(email,password)=>{
        const user=users.find(user.email===email&&user.password===password);
        if(user){
            setCurrentUser(user);
            SetIsAdnim(user.isAdmin);
        }
        else{
            alert('Invalid email or password');
        }
    }


return(
    <UserContext1.Provider value={{users,currentUser,isAdnim,loginUser}}>   
    {children}
    </UserContext1.Provider>
)
}

function AdnimPanel(){
    const{users,isAdmin}=useContext(UserContext1);
    if(!isAdmin){
        return <div>You are not authorized to view this page.</div>;
    }
    return (
        <div>
          <h2>פאנל מנהל</h2>
          <ul>
            {users.map(user => (
              <li key={user.id}>{user.name} ({user.email})</li>
            ))}
          </ul>
          {/* ... כפתורים לעריכת מוצרים וכו' */}
        </div>
      );
}
export {UserProvider,UserContext1,AdnimPanel};
