import React from 'react';
import { Link } from 'react-router-dom';
import logo from '../images/logo.jpg'; // נתיב לתמונהimport { UserContext } from '../Context/UserContext';
import '../css/Navbar.css';
import { FiLogOut } from 'react-icons/fi';  // This is the correct import
import { FaShoppingCart } from 'react-icons/fa'; // אייקון עגלת קניות
import { createContext, useState } from 'react';
import { useCart } from '../Context/CartContext';
import { useContext } from 'react';
import { UserContext } from '../Context/UserContext';


const Navbar = () => {
  const { currentUser, logoutUser } = useContext(useContext);
  const { cartCount } = useCart();
  return (
    <nav className="navbar">
      <Link to="/"> </Link>

      <div className="logo">
        <Link to="/">
          <img src={logo} alt="Pack for Camp Logo" />
        </Link>
      </div>
      <div className="nav-links">
        <Link to="/">Home</Link>
        <Link to="/about">About Us</Link>
        <Link to="/products">Products</Link>
        <Link to="/PerchasePge">PerchasePge</Link>
        <Link to="/cart">Cart</Link>
        <Link to="/contact">Contact Us</Link>
        <div className="cart-icon">
          <Link to="/cart">
            <FaShoppingCart size={24} />
            {cartCount > 0 && <span className="cart-count">{cartCount}</span>}
          </Link>
        </div>

        <div className="user-section">
          {currentUser ? (
            <div className="user-controls">
              <span className="user-greeting">שלום,{currentUser.name}</span>
              <button onClick={logoutUser} className="logout-btn">
                <FiLogOut
                  size={20}
                  className="logout-icon"
                  onClick={logoutUser}
                />  </button>
            </div>
          ) : (
            <div className="auth-links">
              <Link to="/login">Login</Link>
              <Link to="/register">Register</Link>
            </div>
          )}
       
        </div>
      </div>
    </nav>
  );
};
export default Navbar;
