import React from 'react';
import { useCart } from '../Context/CartContext';
import '../css/CartPage.css';
import { useHistory } from 'react-router-dom'; // כדי לבצע מעבר לדף רכישה


const CartPage = () => {
  const { cart, totalAmount, cartCount, increaseQuantity, decreaseQuantity, removeItem } = useCart();
  const history = useHistory();

  const handleProceedToPurchase = () => {
    history.push('/purchase'); // פה תוכל להוסיף את הנתיב לדף הרכישה שלך
  };
  return (
    <div className='cart-page'>
      <h1>Your Cart</h1>
      {cart.length === 0 ? (
        <p className="empty-cart">Your cart is empty</p>
      ) : (
        <ul className="cart-items">
          {cart.map((product, index) => (
            <li key={index} className="cart-item">
              <img 
                src={product.image} 
                alt={product.name} 
                className="item-image"
              />
              <div className="item-details">
                <h3 className="item-name">{product.name}</h3>
                <p className="item-price">₪{product.price}</p>
              </div>
              <div className="quantity-controls">
                <button 
                  className="quantity-btn"
                  onClick={() => decreaseQuantity(product.id)}
                >
                  −
                </button>
                <span className="quantity">{product.quantity}</span>
                <button 
                  className="quantity-btn"
                  onClick={() => increaseQuantity(product.id)}
                >
                  +
                </button>
              </div>
              <button 
                className="remove-btn"
                onClick={() => removeItem(product.id)}
              >
                Remove
              </button>
            </li>
          ))}
        </ul>
      )}
      <div className="cart-summary">
        <h3 className="total">Total: ₪{totalAmount}</h3>
        <h4 className="items-count">Items in cart: {cartCount}</h4>
      </div>
    </div>
  );
};

export default CartPage;
