import React, { useState } from 'react';

const EditProducts = ({ products, updateProduct }) => {
  const [selectedProduct, setSelectedProduct] = useState(null);

  const handleEdit = (product) => {
    setSelectedProduct(product);
  };

  const handleSave = () => {
    updateProduct(selectedProduct); // שימוש בפונקציה לעדכון המוצר
    setSelectedProduct(null); // איפוס המוצר הנבחר
  };

  return (
    <div>
      <h3>עריכת מוצרים</h3>
      {products.map((product) => (
        <div key={product.id}>
          <span>{product.name}</span>
          <button onClick={() => handleEdit(product)}>ערוך</button>
        </div>
      ))}
      {selectedProduct && (
        <div>
          <h4>עריכת מוצר: {selectedProduct.name}</h4>
          <input
            type="text"
            value={selectedProduct.name}
            onChange={(e) =>
              setSelectedProduct({ ...selectedProduct, name: e.target.value })
            }
          />
          <button onClick={handleSave}>שמור</button>
        </div>
      )}
    </div>
  );
};

export default EditProducts;
