import React from 'react';
import { Link } from 'react-router-dom';
import logo from '../images/logo.jpg'; // נתיב לתמונה
import { useContext } from 'react';
import { UserContext } from '../Context/UserContext';
import { useCart } from '../Context/CartContext'; // הוספת CartContext
import { FiLogOut } from 'react-icons/fi';
import { FaShoppingCart } from 'react-icons/fa'; // אייקון סל קניות
import '../css/Navbar.css';

const Navbar = () => {
  const { currentUser, logoutUser } = useContext(UserContext);
  const { cart } = useCart(); // קבלת הסל מקונטקסט

  return (
    <nav className="navbar">
      <div className="logo">
        <Link to="/">
          <img src={logo} alt="Pack for Camp Logo" />
        </Link>
      </div>
      <div className="nav-links">
        <Link to="/">Home</Link>
        <Link to="/about">About Us</Link>
        <Link to="/products">Products</Link>
        <Link to="/PerchasePge">PerchasePge</Link>
        <Link to="/contact">Contact Us</Link>

        {/* אייקון סל קניות */}
        <Link to="/cart">
          <div className="cart-icon">
            <FaShoppingCart size={24} />
            {cart.length > 0 && <span className="cart-count">{cart.length}</span>} {/* הצגת מספר הפריטים בסל */}
          </div>
        </Link>

        <div className="user-section">
          {currentUser ? (
            <div className="user-controls">
              <span className="user-greeting">שלום,{currentUser.name}</span>
              <button onClick={logoutUser} className="logout-btn">
                <FiLogOut size={20} className="logout-icon" />
              </button>
            </div>
          ) : (
            <div className="auth-links">
              <Link to="/login">Login</Link>
              <Link to="/register">Register</Link>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
